#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x -= 0.5; // offset

    // function
    float fx = cos(uv.x*PI*2.+iTime*3.);

    // plotter
    float plot = pow(smoothstep(0.01, 0., abs((fx*0.5+0.5)-uv.y)),1./5.);
    vec3 col = vec3(plot, 0., 0.);
    col = mix(vec3(1.), col, plot);

    fragColor = vec4(col,1.0);
}
