// 计算圆
float Circle(vec2 uv, vec2 offs, float r, float blur) {

    float d = length(uv - offs);
    float c = smoothstep(r, r - blur, d);
    return c;
}

// 微笑
float Smiley(vec2  uv, vec2 offs, float size) {
    uv -= offs;
    uv /= size;
    // 脸
    float mask = Circle(uv, vec2(0), 0.4, 0.01);
    // 眼睛
    mask -= Circle(uv, vec2(-.13, .2), 0.07, 0.01);
    mask -= Circle(uv, vec2(.13, .2), 0.07, 0.01);
    // 嘴
    float mouth = Circle(uv, vec2(0.,0.), .3, .01);
    mouth -= Circle(uv, vec2(0., 0.1), .3, .01);
    mask -= mouth;
    return mask;
}

float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1 * step2;
}

// 矩形
float Rect(vec2 uv, float left, float right, float bottom, float up, float blur) {
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, up, blur);
    return band1 * band2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 转换UV
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // 脸来
    float smiley = Smiley(uv, vec2(0.), 1.);

    // 上色
    vec3 col = vec3(1,1,0);
    vec3 finalColor = col * smiley;

    // 输出
    fragColor = vec4(finalColor,1.0);

    // 测试
    float test = Rect(uv, -.2, .2, -.2, .3, .001);
    fragColor = vec4(test);
}
