vec3 c = vec3(1);
const float AA = 1.; //antialiasing
const float Speed = .1;

void circle(vec2 pos, vec2 pixelPos, float rad, vec3 color) {
    c = mix(c, color, smoothstep(1.0, -1. * AA, length(pos-pixelPos) - rad));
} 


void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  //put circles in order of back to front

  //circle(iMouse.xy, fragCoord, 50., vec3(1, 0, 1));

  for(float j=0.; j < 20.; j++)
  {
      float x = sin(j * iTime * Speed)*200. + 320.;
      float y = cos(j * iTime * Speed)*100. + 180.;
      circle(vec2(x,y), fragCoord, 10.0, vec3(0, 0, 0));
  }

  fragColor = vec4(c, 1.0);
}
