// License Creative Commons Attribution 3.0 Unported License.
// www.mewatools.com
// https://mewatools.com/webstore/index.php?view=BlackAndWhite
//
// uncomment the line below when using this shader in Mewa
//#define MEWA
#ifdef MEWA
#define INPUTPIXEL texture2D(iChannel0, fragCoord)
#else
#define INPUTPIXEL texture(iChannel0, fragCoord.xy/iResolution.xy)
#endif


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float uBrightness = 0.5; // histogram center, brightens/darkens the image. default = 0.5
    const float uSharpness = 1.0; // between 0.001 and 1. default = 0.5

	float dev = uSharpness/2.0;
	float a = uBrightness - dev;
	float b = uBrightness + dev;
    vec4 pixel = INPUTPIXEL;
	float gray = (pixel.r + pixel.g + pixel.b) / 3.0;
	fragColor= vec4( smoothstep(a, b, gray) );
}
