//Inspiration: https://www.shadertoy.com/view/NdByzw

void mainImage( out vec4 O, vec2 I)
{
    vec2 r=iResolution.xy,s=r.xx/15.,
    p=(I+I-r)/(1.+I.y/4e3)-s*iTime*vec2(0,5),
    f=mod(p,s+s),
    a=abs(f/s-1.);
    O=(f.y/s.y)*.1+.8-.8*step(texture(iChannel0,(p-f)/31.).r,vec4(.8,.5,1,0))+
    max(5.-4./(length(a*a*a)),0.);
}
