#define PI 3.14

// 	Colors
#define RED 	vec3(0.00, 0.9, 1.00)
#define BLUE 	vec3(0.04, 0.00, 0.98)
#define BEIGE 	vec3(0.05, 0.45, 0.95)
#define VIOLET 	vec3(0.14, 0.42, 0.96)


#define SPEED 16.0

#define SPIN_SPEED 8.0


//	Usual rotation.
mat2 rotate(float angle)
{
	return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}


//	Convert time to [0; 4 * PI].
float normalizeTime()
{
    float tt = iTime * 1.0;
	return tt - float(int(tt / 4.0 / PI)) * 4.0 * PI;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.xy;

    float time = normalizeTime();

    float angle = abs(acos(dot(rotate(-iTime * SPIN_SPEED) *  rotate(length(uv) * 32.0) * vec2(1, 0), uv) / length(uv)));

    vec2 p = length(uv) * (rotate(angle) * uv);

    float pLength = length(p);

    vec3 color = vec3(pLength);

    float timePercent = time / (4.0 * PI);

    if (angle < 1.5 * abs(0.4 * sin(iTime) + 0.8))
    {
        color = RED;

        if (SPEED * pLength < time)
        {
           color = mix(BEIGE, color, timePercent); 
        }
    }
    else
    {
        color = VIOLET;

        if (SPEED * pLength < time)
        {
            color = mix(BLUE, color, timePercent);
        }
    }

    color *= textureLod(iChannel0, p, 0.5).xyz;

    //	Gamma correction.
    float size = (1.0 + sin(iTime)) / 2.0 * 0.5;
    color = pow(color, vec3(0.4545 * 2.0));
    color *= 13.0 * (size - distance(uv, vec2(0)));
    fragColor = vec4(color,1.0);
}
