float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
float randomindex (float scalar, float st,float time) {
    return floor((scalar-1.0)*rand(vec2(st*rand(vec2(time)),st)));
}
vec3 randomcubes(vec2 i, vec2 st,float scal,float time)
{
    vec2 offset = vec2(randomindex(scal,i.y,time)*(iResolution.x/iResolution.y),randomindex(scal,i.x,time*45.6));
    vec2 coord = st/scal + offset/scal;
    coord.x/=(iResolution.x/iResolution.y);
    return texture(iChannel0, coord).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //fractions
    float scal = 6.0;
    uv.x *=(iResolution.x/iResolution.y);
    uv*=scal;
    vec2 intposition = floor(uv);
    vec2 floatposition = fract(uv);
    uv /= scal ;
    uv.x /=(iResolution.x/iResolution.y);
    //timelapse
    float lapse = step(mod(iTime,4.0),3.0);
    vec3 col = lapse*texture(iChannel0,uv).xyz+(1.0-lapse)*randomcubes(intposition, floatposition,scal,floor(iTime));
    fragColor = vec4(col,1.0);
}
