void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Privent deformation
    float minRes = min(iResolution.x,iResolution.y); 
    //Normalized pixel coordinates 
    vec2 uv = fragCoord/minRes;
    //Initialize mouse position to center
    vec2 disMouse = iMouse.x==0.?uv-.5:uv-iMouse.xy/minRes; 

    //Background gradient color;Sqrt for easing effect
    vec3 col =  mix(vec3(1,.3,.1),vec3(1,.9,.3),sqrt(uv.y)); 

    //Draw leaves
    float r = .2 +.1*cos(atan(disMouse.y,disMouse.x)*10.+20.*disMouse.x+1.);    
    col *= smoothstep(r,r+8./minRes,length(disMouse));

    //Draw trunk
    r = 8./minRes;
    r += 1./minRes*cos(120.0*disMouse.y); //details of trunk
    r += exp(-40.*uv.y); //Ground
    col *= 1.- smoothstep(r+2./minRes,r,abs(disMouse.x-sign(iResolution.x/2.-iMouse.x)*.25*sin(2.*disMouse.y)))*smoothstep(.1,0.,disMouse.y);
    //sign for direction of trunk

    //Output to screen
    fragColor = vec4(col,1.0);
}
