void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float s = 64.0;
    float fade = sin(uv.y+iTime*0.1);
    float wave1 = sin(iTime*0.025);
    float wave2 = cos(iTime*0.001);
    float offset = cos(uv.x+iTime*0.01);
    // Time varying pixel color
    vec3 col = (4.0*fade)*cos(iTime+uv.xyx*vec3(s*wave1,s*wave2,s*offset));

    // Output to screen
    fragColor = vec4(vec3(col.x*col.y*col.z),1.0);
}
