#define BLACK vec3(0.000, 0.000, 0.000)
#define GRAY33 vec3(0.333, 0.333, 0.333) 
#define GRAY 67 vec3(0.667, 0.667, 0.667) 
#define WHITE vec3(1.000, 1.000, 1.000)
#define RED vec3(1.000, 0.000, 0.000)
#define ORANGE vec3(0.894, 0.447, 0.000) 
#define YELLOW vec3(0.894, 0.894, 0.000) 
#define GREEN vec3(0.000, 1.000, 0.000) 
#define CYAN vec3(0.000, 0.707, 0.707)
#define AZURE vec3(0.000, 0.447, 0.894) 
#define BLUE vec3(0.000, 0.000, 1.000) 
#define VIOLET vec3(0.447, 0.000, 0.894) 
#define ROSE vec3(0.894, 0.000, 0.447)
#define BROWN vec3(0.500, 0.250, 0.000)
#define GOLD vec3(0.830, 0.670, 0.220)
#define FOREST_GREEN vec3(0.250, 0.500, 0.250)

float circle(vec2 uv, vec2 p, float r, float blur){
     float d = length(uv-p);
     float c = smoothstep(r, r-blur, d);

     return c;

}float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1*step2;

    }

float rectangle(vec2 uv, float up, float down, float left, float right, float blur){
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, up, down, blur);

    return band1*band2;

    }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float x = uv.x;
    float s = -(x-.5)*(x+.5);
    float y = uv.y;


    float f = circle(uv, vec2(0., 0.), .5, .005);
    float f2 = circle(uv, vec2(0., 0.), .489, .005);

    float F = f - f2;

    float f3 = circle(uv, vec2(0., 0.), .488, .004);
    vec3 F2 = vec3(f3)*(WHITE-vec3(0.95, 0.95, 0.000));

    vec3 k = vec3(F)+F2;

    float eye = circle(vec2(x*.9, (y*.8)+.1), vec2(-0.2, +0.1), .13, .005);
    vec3 Eye = vec3(eye)*(WHITE);
    float eye2 = circle(vec2((x*.9)-.26, (y*.8)+.1), vec2(-0.2, +0.1), .13, .005);
    vec3 Eye2 = vec3(eye2)*(WHITE);

    float ieye = circle(vec2(x*.9, (y*.8)+.1), vec2(-0.2, +0.1), .12, .005);
    vec3 iEye = vec3(ieye)*(WHITE);
    float ieye2 = circle(vec2((x*.9)-.26, (y*.8)+.1), vec2(-0.2, +0.1), .12, .005);
    vec3 iEye2 = vec3(ieye2)*(WHITE);

    vec3 iE = iEye + iEye2;

    vec3 E = Eye + Eye2;

    f2 -= ieye2;

    float p = circle(vec2((x)+.015, (y*.8)+.08), vec2(0., 0.), .15, .005);
    vec3 P = vec3(p)*WHITE;

    vec3 Pupil1 = P*iEye2;

    float p2 = circle(vec2((x)+.2, (y*.8)+.05), vec2(0., 0.), .15, .005);
    vec3 P2 = vec3(p2)*WHITE;

    vec3 Pupil2 = P2*iEye;

    vec3 Pupil = Pupil2 + Pupil1;

    float m = rectangle(vec2(x+.05, y+.25), -.005-s*.1, .005-s*.1, -.1, .1, .005);
    vec3 M = vec3(m)*WHITE;

    float d = circle(uv, vec2(-0.15, -0.27), 0.01, .005);
    vec3 D = vec3(d)*WHITE;
    float d2 = circle(uv, vec2(0.05, -0.275), 0.01, .005);
    vec3 D2 = vec3(d2)*WHITE;

    float b = circle(vec2(x, y*.82), vec2(-0.325, -0.15), .13, .01)*.8;
    float blush = b*f2;
    vec3 Blush1 = vec3(blush)*(WHITE-ORANGE);

    float b2 = circle(vec2(x, y*.82), vec2(0.2, -0.15), .13, .01)*.8;
    float blush2 = b2*f2;
    vec3 Blush2 = vec3(blush2)*(WHITE-ORANGE);

    vec3 Blush = Blush1+Blush2;

    //x += y;

    float line = rectangle(vec2(x-.4, y+.2), -.05-s*.3, .05-s*.3, -.0075+y, .0075+y, .01);
    vec3 Line1 = vec3(line)*WHITE;

    vec3 Mouth = M+D+D2;

    vec3 col = vec3(k)+E-iE-iE+(Pupil+Pupil)+(Mouth+Mouth)+Blush;



    // Output to screen
    fragColor = vec4(WHITE-col,1.0);
}
