const int AMOUNT = 2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 20.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y));

        coord.x *= coord.x - cos(coord.y + sin(len)) + cos(iTime / 9.0);
        coord.y *= coord.y + sin(coord.x + cos(len)) + sin(iTime / 12.0);
    }

    fragColor = vec4(cos(len * 2.6), cos(len * 0.91), cos(len * 2.11), 1.0);
}
