void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);
    //Aspect ratio correction
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime;
    vec2 cpos = uv;// centre position
    float angle = atan(cpos.y,cpos.x);
    float radius = sqrt(cpos.x*cpos.x + cpos.y*cpos.y);

    float radialIntensity =pow(sin(radius*64.) * 0.5 + 0.5, 2.0);
    float angleIntensity = pow(sin(angle*.5+t*3.) * 0.5 +0.5, 7.0);
    float intensity = radialIntensity*angleIntensity;

    fragColor = vec4(0., 0.05, 0.02, 0.);
    fragColor += vec4(intensity*.1, intensity*.6, intensity*.2, 1.0);

}
