// Inspired by visuals in Max Cooper - Void 
// https://youtu.be/3gzvehNmmAM
vec3 boxes(vec2 p, vec2 m) 
{
	vec3 col = vec3(0.0); 

	float lt = 0.004; 
	float sc = 1.0;
	float w = 0.5;
	float h = 1.0;
	for (int i = 0; i < 50; i++) {

		if (abs(p.x) < w && abs(p.y) < h) {
			if (abs(p.x) > w-lt || abs(p.y) > h-lt) 
				col = vec3(1)*sc; 
		}

		w -= 0.01;
		h -= 0.02;
		p += vec2(m.x-0.5,m.y-0.5);
		//p *= 1.2;
		sc *= 0.95;
	}
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = 2.0*( fragCoord.xy / iResolution.xy ) -1.0;

	p.x *= iResolution.x/iResolution.y;
	vec3 col = vec3(0);


    vec2 m = vec2(0.5)+vec2(cos(iTime*0.7),sin(iTime*0.4))*0.01; 
	//vec2 m = iMouse.xy*0.001;
	for (int i = 0; i < 10; i++) {
		float a = 3.1415*float(i)/10.0;
		vec2 offs = vec2(cos(a),sin(a))*0.0025; 
		col += boxes(p+offs-vec2(-1.00,0.0), m.xy);
		col += boxes(p+offs-vec2(-0.0,0.0), m.yx);
		col += boxes(p+offs-vec2(+1.00,0.0), 1.0-m.xy);

	}
	col /= 10.0;
	fragColor = vec4(col, 1.0);

}
