// set this to 2 for higher image quality
#define AA 1

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

vec3 texturize( sampler2D sa, in vec3 p, in vec3 n )
{
	vec3 x = texture( sa, p.yz ).xyz;
	vec3 y = texture( sa, p.zx ).xyz;
	vec3 z = texture( sa, p.xy ).xyz;
	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}

vec4 disp( vec3 p )
{
	p -= 3.0*(-1.0+2.0*noise( 0.005*p - 0.25*iTime)) * cos(2.0*iTime);

	float off = iTime*0.5 + 0.4*sin(iTime*2.0);

	vec3 q = p*0.05*vec3(1.0,.8,1.0);
    float f = 0.0;	
	f += 0.500*noise( q ); q *= 2.1; q.y += off;
	f += 0.250*noise( q ); q *= 2.0; q.y += off;
	f += 0.125*noise( q );

	return vec4( f, q );
}

float torusSDF( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

vec3 map( in vec3 p )
{
	vec4 dd = disp( p );

	float f = dd.x;

	dd.yzw *= 6.;
	float g = sin(dd.y)*sin(dd.z)*sin(dd.w) * (1.0-smoothstep( -5.0, 20.0, p.y ));
	f += f*0.1*g;

	float d = length(p) - (20.+sin(iTime*4.)*4.) * f;//p.x - 20.0*f;

	d *= 0.8;

	return vec3( d, f, g );
}

vec3 intersect( in vec3 ro, in vec3 rd )
{
	vec3 res = vec3(1e10,-1.0, -1.0);

	float maxd = 200.0;
	float precis = 0.002;
    float h = 1.0;
    float t = 0.0;
    float m = -1.0;
    float g = -1.0;
    for( int i=0; i<128; i++ )
    {
        if( h<precis||t>maxd ) break;
	    vec3 res = map( ro+rd*t );
        h = res.x;
		m = res.y;
		g = res.z;
        t += h;
    }
	if( t<maxd ) res=vec3(t,m,g);

	return res;
}

float softshadow( in vec3 ro, in vec3 rd, float k )
{
    float res = 1.0;
    float t = 0.01;
	float h = 1.0;
    for( int i=0; i<64; i++ )
    {
        h = map(ro + rd*t).x;
        res = min( res, max(k*h/t,0.0) );
		t += clamp( h, 0.02, 0.1 );
    }
    return clamp(res,0.0,1.0);
}

vec3 shade( vec3 pos, vec3 nor, vec3 rd, float occ, float di, float t )
{
    // materual
	vec3  ref = reflect(rd,nor);
	vec4  rr  = 16.0*disp( pos );
    float f   = smoothstep( -5.0, 20.0, pos.y );
	float cm = smoothstep(-0.5,1.0,di+f);
	vec3 col = mix( vec3(1.0,0.95,0.65), 0.9*vec3(1.0,0.6,0.2 ), cm );

	col *= mix( vec3(1.0), 0.2+1.0*texturize( iChannel1, 0.001*rr.yzw, nor ), 0.25+0.75*f );
	col *= vec3(1.7,.8,1.) ;
	// lighting
	occ *= pos.x/(20. +sin(iTime*5.) *13.0);
    occ *= pos.z/(30.0 +sin(iTime*5.)*13.0);

    occ *= 1.25;
	float sss = clamp(1.5*(occ*occ*0.5+0.5*occ), 0.0, 1.0 );
	float rha = softshadow( pos+0.1*nor, ref, 4.0 );
    vec3  lin =  0.3*pow(texture( iChannel3, nor ).xyz,vec3(2.0))*occ;	
          lin += 1.0*sss*vec3(1.1,0.70,0.7);

    // combine	
	col = lin*col;
    col += (0.2+0.8*cm)*0.15*rha*pow(texture( iChannel2, reflect(rd,nor)).xyz,vec3(2.0));

	return col * exp(-0.0001*t*t);
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.1,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
}

vec3 render( in vec2 p, in mat4 cam )
{
	vec2 q = p / iResolution.xy;

    // create view ray
    const float fle = 2.0;

    vec2  sp = (-iResolution.xy + 2.0*p) / iResolution.y;
    vec3  ro = vec3( cam[0].w, cam[1].w, cam[2].w );
	vec3  rd = normalize( (cam*vec4(sp,fle,0.0)).xyz );


//    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

    //-----------------------------------------------------
    // render
    //-----------------------------------------------------
    vec3 res = 	intersect(ro,rd);
    float t = res.x;
    vec3 pos = ro + t*rd;
    vec3 nor = calcNormal( pos );
    vec3 col = shade( pos, nor, rd, res.y, res.z, t );

    // to screen	 
    col = max( col, 0.0 );
    col = pow( col, vec3(0.45) ); 

    //-----------------------------------------------------
    // post
    //-----------------------------------------------------
    col = col*col*(3.0-2.0*col);
    col *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.1 );

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------
	float an = 1.0;
	vec3 ro = 40.0*vec3(sin(an),0.0,cos(an));
    vec3 ta = vec3(0.0,0.0,0.0);
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    mat4 cam = mat4( uu, ro.x, vv, ro.y, ww, ro.z, 0.0, 0.0, 0.0, 1.0 );

    // render
    #if AA<2
	vec3 col = render(  fragCoord, cam );
    #else
    vec3 col = vec3(0.0);
    for( int j=0; j<AA; j++ )
    for( int i=0; i<AA; i++ )
    {
	    col += render( fragCoord + (vec2(i,j)/float(AA)), cam );
    }
	col /= float(AA*AA);
    #endif


	fragColor = vec4( col, 1.0 );
}
