// Orginal piece by @zozuar
// https://twitter.com/zozuar/status/1521538248522272769
float PI = 3.1415926538;

mat2 Rotate(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec3 point,color;

    float g,e,s;
    float newY;
    float k=iTime/3.;
    vec2 coord = vec2((gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y);

    for(int i;i<90;i++){
        color+=.014/exp(e*1000.);

        point=vec3(coord*g,g)+0.5;
        point.yz*=Rotate(sin(k)*.13+.0001);
        point.xz*=Rotate(k);
        point.z-=iTime/PI;
        point++;

        newY=point.y*.3-.3;
        s=8.;

        for(int j;j++<9;point/=e) {
            point=mod(point-1.001,2.0)-1.;
            point.zx*=Rotate(PI/4.);
            s/=e=dot(point,point)*(.6+newY);                        
        }

        g+=e=sqrt(e)/s;
     }

     fragColor = vec4(vec3(color.x,g/22.+color.x/3.,e+color.x/3.),1.0);  
}
