#define ROT(v) mat2(cos(v),-sin(v),sin(v),cos(v))
void mainImage( out vec4 f,  vec2 t )
{
    // Normalized pixel coordinates (from 0 to 1)
    t = t/min(iResolution.x,iResolution.y);
t.x -= 0.9, t.y -= 0.7;

float s = (30.*(abs(t.x*3.14*2.)/3.14)/2.71) 
    + sin(6.28*t.y/1.
    -sqrt(abs(t.x/(t.y
    -1.))*1.)*3.14)*6.28;

    float a =  dot((t.y-0.1)/18.,(t.y-10.)+40.);

    vec3 HSV = (clamp(
    abs(
    fract(
    a-vec3(0.,2.,1.)/3.0
    )
    *6.-2.76
    )-1.0,0.0,1.0
    )-1.0);
// Output to screen

  vec3  co = vec3(1.0);
  co = co/step(s,t.y)+HSV;

    f = vec4(co,1.0);
}
