#define PI 3.1415926538
#define TAU PI * 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = iTime * 0.5;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.y *= iResolution.y / iResolution.x;
    vec2 modedUv = vec2(
      sin(uv.x * (sin(0.025 * time)+1.0)*3.0 + 0.5 * time),
      cos(uv.y * (cos(0.025 * time)+1.0)*1.0 + 0.5 * time)
    );
    vec2 sinUv = sin(modedUv + time);
    sinUv.x -= cos(sinUv.y - time);
    float tauProgress = atan(sinUv.x, sinUv.y) + PI;
    tauProgress /= TAU;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(time * 0.01+vec3(tauProgress)+vec3(0,2,4));

    vec3 black = vec3(1.0) - col;

    float distFromOrigin = abs(distance(uv, vec2(0.0, 0.0)) - tauProgress * 0.2);
    float strokeWidth = 0.05;
    float circle = smoothstep(strokeWidth, strokeWidth + 0.0025, distFromOrigin);

    vec3 mixed = mix(black, col, circle);


    // Output to screen8
    fragColor = vec4(mixed,1.0);
}
