void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =50.0 * fragCoord/iResolution.xy-25.0;
    float x = uv.x - sin(iMouse.x / 100.0);
    float y = uv.y * (iResolution.y / iResolution.x);
    float a = sin(iTime);
    vec3 col;

    //vec3 col =vec3(0.8, y - a / 1000.0 - sin(3.0 * y / (x + a * y)) ,0.5);
     x /= 4.0;
     y/= -4.0;
     float c = sin(x  / y * a * a);
     c = 0.01 / c;
    if (c >= 0.0) {
    col = vec3(c  ,0.0 ,0.0);
    }
    else {
    col = vec3( 0.0,0.0, -c);
    }

    fragColor = vec4(col,1.0);
}
