void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Output to screen
    fragColor = texture(iChannel0, uv) / float(iFrame + 1);
}

#define EPS 0.000001
#define saturate(oo) clamp(oo, 0.0, 1.0)

#define MarchSteps 2
#define ExpPosition vec3(0, -0.5, 0)
#define Radius 0.3
#define NoiseSteps 1
#define NoiseAmplitude 0.1
#define NoiseFrequency 6.0

// Colour Gradient
#define Color1 vec4(1.0, 1.0, 1.0, 1.0)
#define Color2 vec4(1.0, 0.8, 0.2, 1.0)
#define Color3 vec4(1.0, 0.03, 0.0, 1.0)
#define Color4 vec4(0.05, 0.02, 0.02, 1.0)

const vec3 CAMERA_POS = vec3(2, 1.2, -6);
const float INF = 1e10;

vec3 LIGHT1_POS = vec3(-4, 1.5, 5);
const float LIGHT1_RADIUS = 0.7;
const vec3 LIGHT1_COLOR = vec3(0.5, 0.9, 0.3);

vec3 LIGHT2_POS = vec3(2, 1, 2);
const float LIGHT2_RADIUS = 0.3;
const vec3 LIGHT2_COLOR = vec3(0.5, 0.1, 0.9);

vec3 randDir;

struct Triangle {
	vec3 v0;
    vec3 v1;
    vec3 v2;
};


float tracePlane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.2 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }

    vec3 worldPos = t * dir + pos;
    if (dot(worldPos.xz, worldPos.xz) >= 100.0) {
        return INF;
    }
    normal = vec3(0, 1, 0);
    return t;
}

float traceTriangle(vec3 pos, vec3 dir, out vec3 normal, Triangle obj) {
    vec3 d1 = obj.v1 - obj.v0;
    vec3 d2 = obj.v2 - obj.v0;
    vec3 dPos = pos - obj.v0;
    vec3 v1 = cross(d1, d2);
    vec3 v2 = cross(dPos, dir);
    float d = 1.0 / dot(dir, v1);

    if (abs(d) < EPS) {
        return INF;
    }

    float t = d * dot(-v1, dPos);
    float y = d * dot(-v2, d2);
    float z = d * dot(v2, d1);

    if (y < 0.0 || z < 0.0 || y + z > 1.0) {
        return INF;
    }

    normal = normalize(cross(d1, d2));
    if (dot(normal, dir) > 0.0) {
        normal = normalize(cross(d2, d1));
    }

    if (t <= 0.0) {
        return INF;
    }

    return t;
}

vec3[5] tops = vec3[5] (
    vec3(0, 1, 0),
    vec3(-1, -1, -1),
    vec3(1, -1, -1),
    vec3(1, -1, 1),
    vec3(-1, -1, 1)
);

float tracePyramid(vec3 pos, vec3 dir, out vec3 normal) {
    Triangle t1 = Triangle(tops[2], tops[1], tops[0]);
    Triangle t2 = Triangle(tops[0], tops[2], tops[3]);
    Triangle t3 = Triangle(tops[0], tops[3], tops[4]);
    Triangle t4 = Triangle(tops[0], tops[4], tops[1]);
    Triangle t5 = Triangle(tops[1], tops[2], tops[3]);
    Triangle t6 = Triangle(tops[1], tops[4], tops[3]);

    Triangle triangles[6] = Triangle[6](t1, t2, t3, t4, t5, t6);
    vec3 tmpNormal;
    float t = INF;
    for (int i = 0; i < 6; i++) {
        float triT = traceTriangle(pos, dir, tmpNormal, triangles[i]);
        if (triT < t) {
            t = triT;
            normal = tmpNormal;
        }
    }
    return t;
}

float traceSphere(vec3 pos, vec3 dir, float r, out vec3 normal) {
    float a = dot(dir, dir);
    float b = dot(pos, dir);
    float c = dot(pos, pos) - r * r;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    float t = -b - sqrt(D);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(D);
    if (t < 0.0) {
        return INF;
    }
    normal = normalize(pos + t * dir);
    return t;
}

float traceCylinder(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.0 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = t * dir + pos;
    if (dot(worldPos.xz, worldPos.xz) < 3.0) {
        normal = vec3(0, 1, 0);
        return t;
    }

    float a = dot(dir.xz, dir.xz);
    float b = dot(pos.xz, dir.xz);
    float c = dot(pos.xz, pos.xz) - 3.0;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    t = (-b - sqrt(D)) / a;
    if (t > 0.0) {
        worldPos = t * dir + pos;
        if (worldPos.y <= -1.0) {
            normal = normalize(vec3(worldPos.x, 0, worldPos.z));
            return t;
        }
    }
    t = (-b + sqrt(D)) / a;
    if (t < 0.0) {
        return INF;
    }
    worldPos = t * dir + pos;
    if (worldPos.y <= -1.0) {
        normal = normalize(vec3(worldPos.x, 0, worldPos.z));
        return t;
    }
    return INF;
}

bool isOccluded(vec3 pos, vec3 target) {
    vec3 dir = target - pos;
    float dist = length(dir);
    dir /= dist;

    vec3 pyrNorm;
    float pyrT = tracePyramid(pos, dir, pyrNorm);
    if (pyrT < dist)
        return true;
    vec3 cylNorm;
    float cylT = traceCylinder(pos, dir, cylNorm);
    return (cylT < dist);
}

vec3 computeLight(vec3 pos, vec3 color, vec3 normal) {
    vec3 toLight1 = LIGHT1_POS - pos;
    float distSq1 = dot(toLight1, toLight1);
    float att1 = isOccluded(pos, LIGHT1_POS + randDir * LIGHT1_RADIUS) ? 0.0 : 30.0f / distSq1;
    vec3 toLight2 = LIGHT2_POS - pos;
    float distSq2 = dot(toLight2, toLight2);
    float att2 = isOccluded(pos, LIGHT2_POS + randDir * LIGHT2_RADIUS) ? 0.0 : 15.0f / distSq2;
    return color * (
        max(0.0, dot(normal, normalize(toLight1))) * att1 * LIGHT1_COLOR
        + max(0.0, dot(normal, normalize(toLight2))) * att2 * LIGHT2_COLOR
        + texture(iChannel1, normal).rgb * 0.1
    );
}

vec3 refraction(vec3 v_in, vec3 normal, float n1, float n2) {
    if (dot(v_in, normal) < 0.0)
        normal = -normal;
    float cosA = dot(v_in, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v_in - cosA * normal);
    float sinB = n1 * sinA / n2;
    float cosB = sqrt(1.0 - sinB * sinB);
    return tang * sinB + normal * cosB;
}

float pow2(float x) {
    return x * x;
}

float rand(float frame)
{
    return fract(sin( dot(vec3(frame), vec3(12.9898,78.233,45.5432) )) * 43758.5453);
}

vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 permute(vec4 x) { return mod289(((x*34.0)+1.0)*x); }
vec4 taylorInvSqrt(vec4 r){ return 1.79284291400159 - 0.85373472095314 * r; }

float fireballSnoise(vec3 v)
{
	const vec2  C = vec2(1.0/6.0, 1.0/3.0);
	const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);
	vec3 i  = floor(v + dot(v, C.yyy));
	vec3 x0 = v - i + dot(i, C.xxx);
	vec3 g = step(x0.yzx, x0.xyz);
	vec3 l = 1.0 - g;
	vec3 i1 = min(g.xyz, l.zxy);
	vec3 i2 = max(g.xyz, l.zxy);
	vec3 x1 = x0 - i1 + C.xxx;
	vec3 x2 = x0 - i2 + C.yyy;
	vec3 x3 = x0 - D.yyy;
	i = mod289(i);
	vec4 p = permute( permute( permute( i.z + vec4(0.0, i1.z, i2.z, 1.0)) + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));
	float n_ = 0.142857142857;
	vec3  ns = n_ * D.wyz - D.xzx;
	vec4 j = p - 49.0 * floor(p * ns.z * ns.z);
	vec4 x_ = floor(j * ns.z);
	vec4 y_ = floor(j - 7.0 * x_);
	vec4 x = x_ *ns.x + ns.yyyy;
	vec4 y = y_ *ns.x + ns.yyyy;
	vec4 h = 1.0 - abs(x) - abs(y);
	vec4 b0 = vec4(x.xy, y.xy);
	vec4 b1 = vec4(x.zw, y.zw);
	vec4 s0 = floor(b0) * 2.0 + 1.0;
	vec4 s1 = floor(b1) * 2.0 + 1.0;
	vec4 sh = -step(h, vec4(0.0));
	vec4 a0 = b0.xzyw + s0.xzyw * sh.xxyy;
	vec4 a1 = b1.xzyw + s1.xzyw * sh.zzww;
	vec3 p0 = vec3(a0.xy, h.x);
	vec3 p1 = vec3(a0.zw, h.y);
	vec3 p2 = vec3(a1.xy, h.z);
	vec3 p3 = vec3(a1.zw, h.w);
	vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
	p0 *= norm.x;
	p1 *= norm.y;
	p2 *= norm.z;
	p3 *= norm.w;
	vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
	m = m * m;
	return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3)));
}

float Turbulence(vec3 position, float minFreq, float maxFreq, float qWidth)
{
	float value = 0.0;
	float cutoff = clamp(0.5/qWidth, 0.0, maxFreq);
	float fade;
	float fOut = minFreq;
	for(int i=NoiseSteps ; i>=0 ; i--)
	{
		if(fOut >= 0.5 * cutoff) break;
		fOut *= 2.0;
		value += abs(fireballSnoise(position * fOut))/fOut;
	}
	fade = clamp(2.0 * (cutoff-fOut)/cutoff, 0.0, 1.0);
	value += fade * abs(fireballSnoise(position * fOut))/fOut;
	return 1.0-value;
}

float SphereDist(vec3 position)
{
	return length(position - ExpPosition) - Radius;
}

vec4 Shade(float distance)
{
	float c1 = saturate(distance*5.0 + 0.5);
	float c2 = saturate(distance*5.0);
	float c3 = saturate(distance*3.4 - 0.5);
	vec4 a = mix(Color1,Color2, c1);
	vec4 b = mix(a,     Color3, c2);
	return 	 mix(b,     Color4, c3);
}

float RenderScene(vec3 position, out float distance)
{
	float noise = Turbulence(position * NoiseFrequency, 0.1, 1.5, 0.03) * NoiseAmplitude;
	noise = saturate(abs(noise));
	distance = SphereDist(position) - noise;
	return noise;
}

vec4 March(vec3 rayOrigin, vec3 rayStep)
{
	vec3 position = rayOrigin;
	float distance;
	float displacement;
	for(int step = MarchSteps; step >=0  ; --step)
	{
		displacement = RenderScene(position, distance);
		if(distance < 0.05) break;
		position += rayStep * distance;
	}
	return mix(Shade(displacement), vec4(0), float(distance >= 0.5));
}

bool IntersectSphere(vec3 ro, vec3 rd, vec3 pos, float radius, out vec3 intersectPoint)
{
	vec3 relDistance = (ro - pos);
	float b = dot(relDistance, rd);
	float c = dot(relDistance, relDistance) - radius*radius;
	float d = b*b - c;
	intersectPoint = ro + rd*(-b - sqrt(d));
	return d >= 0.0;
}
vec3 fireball(vec2 p) {
    float rotx = 0.6;
	float roty = 0.8;
	float zoom = 10.0;
	vec3 ro = zoom * normalize(vec3(cos(roty), cos(rotx), sin(roty)));
	vec3 ww = normalize(vec3(0.0, 0.0, 0.0) - ro);
	vec3 uu = normalize(cross( vec3(0.0, 1.0, 0.0), ww));
	vec3 vv = normalize(cross(ww, uu));
	vec3 rd = normalize(p.x*uu + p.y*vv + 1.5*ww);
	vec4 col;
	vec3 origin;
	if(IntersectSphere(ro, rd, ExpPosition, Radius + NoiseAmplitude*6.0, origin))
	{
		col = March(origin, rd);
	}
    return vec3(col.r, col.g, col.b);
}

float burnSnoise(vec3 uv, float res)
{
	const vec3 s = vec3(1e0, 1e2, 1e3);
	uv *= res;
	vec3 uv0 = floor(mod(uv, res))*s;
	vec3 uv1 = floor(mod(uv+vec3(1.), res))*s;
	vec3 f = fract(uv);
	f = f*f*(3.0-2.0*f);
	vec4 v = vec4(uv0.x+uv0.y+uv0.z, uv1.x+uv0.y+uv0.z,
	              uv0.x+uv1.y+uv0.z, uv1.x+uv1.y+uv0.z);
	vec4 r = fract(sin(v*1e-1)*1e3);
	float r0 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);
	r = fract(sin((v + uv1.z - uv0.z)*1e-1)*1e3);
	float r1 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);
	return mix(r0, r1, f.z) * 2.0 - 1.0;
}

vec3 burn(vec2 p)
{
    p.y += 0.05;
	float color1 = 2.0 - (2.0 * length(2.5 * p));
	vec3 coord = vec3(atan(p.x, p.y) / 6.2832 + 0.5, length(p) * 0.4, 0.5);
	for(int i = 1; i <= 3; i++)
	{
		float power = pow(2.0, float(i));
		color1 += 0.5*(1.5 / power) * burnSnoise(coord + vec3(0.0, -0.05, -0.01), power * 16.0);
	}

    return vec3( color1, pow(max(color1, 0.0), 2.0)*0.4, pow(max(color1, 0.0), 3.0) * 0.05);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    randDir = normalize(randVals - 0.5);
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (randVals.xy - 0.5)) / iResolution.x;
    vec3 front = normalize(-CAMERA_POS);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(front, up));
    up = normalize(cross(right, front));
    vec3 viewVec = normalize(front + right * uv.x + up * uv.y);
    vec4 globColor = vec4(0, 0, 0, 0);

    const int EMISSION = 0;
    const int DIFFUSE = 1;
    const int REFLECTION = 2;
    const int REFRACTION = 3;

    vec3 curPos = CAMERA_POS;
    vec3 curDir = viewVec;
    const float AIR_N = 1.0;
    const float GLASS_N = 1.5;
    float n1 = AIR_N;
    float GLASS_R = pow2(AIR_N - GLASS_N) / pow2(AIR_N + GLASS_N);
    LIGHT1_POS = LIGHT1_POS + randVals*0.1;
    LIGHT2_POS = LIGHT2_POS + randVals*0.1;


    for (int i = 0; i < 10; ++i) {
        float t = INF;
        int materialType;
        vec3 color;
        vec3 normal;
        float nEnter = AIR_N;

        vec3 planeNorm;
        float planeT = tracePlane(curPos, curDir, planeNorm);
        if (planeT < t) {
            t = planeT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel0, worldPos.xz * 0.1).rgb;
            normal = planeNorm;
        }

        vec3 l1Norm;
        float light1T = traceSphere(curPos - LIGHT1_POS, curDir, LIGHT1_RADIUS, l1Norm);
        if (light1T < t) {
            t = light1T;
            materialType = EMISSION;
            color = LIGHT1_COLOR;
            normal = l1Norm;
        }

        vec3 l2Norm;
        float light2T = traceSphere(curPos - LIGHT2_POS, curDir, LIGHT2_RADIUS, l2Norm);
        if (light2T < t) {
            t = light2T;
            materialType = EMISSION;
            color = LIGHT2_COLOR;
            normal = l2Norm;
        }

        vec3 cylNorm;
        float cylT = traceCylinder(curPos, curDir, cylNorm);
        if (cylT < t) {
            t = cylT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel2, worldPos.xz * worldPos.y).rgb;
            normal = cylNorm;
        }

        vec3 pyramidNorm;
        float pyrT = tracePyramid(curPos, curDir, pyramidNorm);
        if (pyrT < t) {
            t = pyrT;
            normal = pyramidNorm;
            if (dot(curDir, normal) > 0.0) {
                nEnter = AIR_N;
            } else {
                nEnter = GLASS_N;
            }
            if (randVals.x < GLASS_R * 10.0) {
                materialType = REFLECTION;
            } else {
                materialType = REFRACTION;
            }
            vec2 p = (fragCoord.xy / iResolution.xy) - 0.5;
            p.x *= iResolution.x/iResolution.y;
            globColor += vec4(mix(fireball(p), burn(p) * 0.5, .3), 1.0);
        }


        if (t != INF) {
            vec3 worldPos = t * curDir + curPos;
            globColor += vec4(texture(iChannel0, worldPos.xz).rgb * 0.1, 0.0); 
            if (materialType == EMISSION) {
                globColor += vec4(color, 0.0);
                break;
            } else if (materialType == DIFFUSE) {
                globColor += vec4(computeLight(worldPos, color, normal), 0.0);
                break;
            } else if (materialType == REFLECTION) {
                curDir = reflect(curDir, normal);
                curPos = worldPos + curDir * 1e-5;
            } else if (materialType == REFRACTION) {
                curDir = refraction(curDir, normal, n1, nEnter);
                curPos = worldPos + curDir * 1e-5;
                n1 = nEnter;
            }
        } else {
            globColor += vec4(texture(iChannel1, curDir).rgb * 0.1, 0.0);
        }
    }
    fragColor = globColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
}
