float STEP_VALUE = 0.2;
float WAVE_FREQUENCY = 8.0;
float SPEED = 4.0;

#define Pi 3.141516

float rand1(float x){
    return fract(sin(x)*1000.0);
}

float rand2(float x){
    return fract(sin(x)*10000.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Time varying pixel color
    vec2 newUv = uv*2.0 - 1.0;
    float dist = length(newUv);
    float cossinedDist = cos(dist*Pi*WAVE_FREQUENCY+iTime*SPEED);
    float steppedDist = step(STEP_VALUE,dist);
    float randomNoise = rand1(rand2(fragCoord.x) + rand1(fragCoord.y)+iTime);
    vec3 col = vec3(cossinedDist*randomNoise );//+ (1.0-cossinedDist)*texture(iChannel2,uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
