//Try changing this parameter to get more crazy patterns!
#define s1 2.0

#define ITERS 12
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/20.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){


        for(int i=0;i<ITERS;i++)
        {

            uv= fract(-uv-((vec2(uv.x/scale-uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))/scale1;
            uv.x *= scale1/s1;
            uv = -fract(-uv.yx);
            uv.y /= -scale1/s1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}
