void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    float unit = 3.0 / iResolution.y;

    float curTime = float(iFrame) * dt;

    vec2 linkOrigin = vec2(0.0);
    for (int link=0; link < LINKS; link++) {
        vec3 curLink = getLink(link);
        float curAngle = curLink.y * curTime + curLink.x;
        vec2 linkEnd = linkOrigin + vec2(cos(curAngle), sin(curAngle)) * curLink.z;
        fragColor.rgb += smoothstep(unit, 0.0, sdLine(uv, linkOrigin, linkEnd));
        linkOrigin = linkEnd;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    float unit = 2.0 / iResolution.y;

    float curTime = float(iFrame) * dt;
    float prevTime = curTime - dt;

    vec2 prevPos = vec2(0.0);
    vec2 curPos = vec2(0.0);
    for (int link=0; link < LINKS; link++) {
        vec3 curLink = getLink(link);

        float prevAngle = curLink.y * prevTime + curLink.x;
        prevPos += vec2(cos(prevAngle), sin(prevAngle)) * curLink.z;

        float curAngle = curLink.y * curTime + curLink.x;
        curPos += vec2(cos(curAngle), sin(curAngle)) * curLink.z;
    }

    fragColor.rgb = mix(fragColor.rgb, hue2rgb(fract(0.25 * iTime)), smoothstep(unit, 0.0, sdLine(uv, prevPos, curPos) - 0.001));
    fragColor.rgb *= 0.99;
}

#define hue2rgb(t) clamp(abs(6.0 * fract(vec3(t, t - 1.0 / 3.0, t - 2.0 / 3.0)) - 3.0) - 1.0, 0.0, 1.0)

#define LINKS 50
#define dt 0.02

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
vec3 Hash13(in float x) {
   vec3 p3 = fract(x * vec3(0.1031, 0.1030, 0.0973));
   p3 += dot(p3, p3.yzx + 33.33);
   return fract((p3.xxy + p3.yzz) * p3.zyx); 
}

vec3 getLink(in int n) {
    float i = float(n);
    vec3 link = Hash13(i + 10.0);
    link.x = link.x * 2.0 - 1.0;
    link.y *= i * i * 0.01 * sign(Hash13(i + 13.0).x - 0.5);
    //link.y = 0.25 * i * sign(Hash13(i + 13.0).x - 0.5);
    link.z *= 0.1;
    return link;
}
