void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 blocks = floor((fragCoord.xy-iResolution.xy*.5)*.1);
    vec3 mul = vec3(0.5 + 0.5 * sin(-10.*iTime + length(blocks)*.1 + mod(blocks.y,2.)));
    vec3 bgCol = vec3(sin((iTime+atan(blocks.y,blocks.x)+length(blocks))), .7 + 0.5*sin(blocks.y),.8)+0.5*sin(iTime + blocks.xyy + vec3(0,2,4));
    for(int i = 0; i < 4; i++) {
        bgCol = bgCol.yzx;
        bgCol.x*=bgCol.y*1.1;
    }    
    fragColor = vec4(bgCol*mul+bgCol*(0.7+0.1*sin(iTime)),1.0);
}
