//Pixelated Portal
//by Sam Gates (BackwardsCap)


void mainImage( out vec4 c, in vec2 f )
{
    c.rgb = vec3(0);
    float downscale = 10.;
    f /= downscale;
    f = floor(f);
    f *= downscale;

	c.rgb = S0(f).rgb/2.0+S1(f).rgb*.8;    
    vec2 p = (2.0*f-R)/R.y;

    float a = (length(p)-1.)/2.;
    vec3 ba = vec3(0);
    for(int y=-4;y<=4;y++)
    for(int x=-4;x<=4;x++)
    {
        vec2 o = vec2(x,y);
        if(length(o)<66.)
        ba+= S1(f+o).rgb;
    }

    ba/=15.0;

    c.rgb+=ba*a;
}

#define R iResolution.xy
#define PI 3.1415927
#define m vec2(5.+2.*4.,10.)
#define SS(U) smoothstep(PX, 0., U)

#define MORPH

void mainImage( out vec4 c, in vec2 f )
{
    c.rgb = vec3(0);
    vec2 p = ((2.0*f-R)/R.y), u=p, r;

    p = vec2(log(length(p.xy)), atan(p.y,p.x));
    p*= (6.0/PI)* 10.85;
    p+=vec2(5.,3.); 
    p.y+=iTime*2.0;
    p.y = mod(p.y,10.015*13.0)-.2;
    vec2 lp = (p);    

    if(p.x>=-5.&&p.x<=4.)
	    c.rgb=vec3(clamp(1.-length(mod(lp,5.) - vec2(1,3. + sin(iTime))),0.,1.));    
}

void F(vec2 p, vec2 offset, vec4 s, inout vec2 v, inout float pr, inout float d, inout float n)
{
    vec4 target = S0(p+offset-V(p+offset).xy);
    offset=normalize(offset);
    v+=offset*(target.w-s.w);
    pr+=target.w;
    d+=dot(offset,target.xy);
    n++;
}

void mainImage(out vec4 s, vec2 p)
{
    s.rgb = vec3(0);
    s = S0(p-V(p).xy);
    vec2 v = vec2(0), u = (2.0*p-R)/R.y;
    float d = 0., pr=0.,n=0.;

    F(p, vec2(0,1), s, v, pr, d, n);
    F(p, vec2(0,-1), s, v, pr, d, n);
    F(p, vec2(1,0), s, v, pr, d, n);
    F(p, vec2(-1,0), s, v, pr, d, n);

    d/=n;
    pr/=n;
    v/=n;

    s.w = pr-d;
	s.xy -= v;   
    s.z = d;

    s.xy*=decay;

    if(S1(p).r>0.)
    {
        s.xy = -u*sin(iTime - u.x)/2.;
    }


    if (p.x<1.||p.y<1.||R.x-p.x<1.||R.y-p.y<1.) s.xy *= 0.;
}

void F(vec2 p, vec2 offset, vec4 s, inout vec2 v, inout float pr, inout float d, inout float n)
{
    vec4 target = S0(p+offset-V(p+offset).xy);
    offset=normalize(offset);
    v+=offset*(target.w-s.w);
    pr+=target.w;
    d+=dot(offset,target.xy);
    n++;
}

void mainImage(out vec4 s, vec2 p)
{
    s.rgb = vec3(0);
    s = S0(p-V(p).xy);
    vec2 v = vec2(0);
    float d = 0., pr=0.,n=0.;

    F(p, vec2(0,1), s, v, pr, d, n);
    F(p, vec2(0,-1), s, v, pr, d, n);
    F(p, vec2(1,0), s, v, pr, d, n);
    F(p, vec2(-1,0), s, v, pr, d, n);

    d/=n;
    pr/=n;
    v/=n;

    s.w = pr-d;
	s.xy -= v;   
    s.z = d;
}

void mainImage(out vec4 state, vec2 f)
{
    state.rgb = vec3(0);
    for(int i=0;i<2;i++)
    f -= S1(f).xy;

    state = S0(f);

    state*=decay;
    float t = iTime/5.0;

    if(S2(f).r>0.1)
        state.rgb += vec3((cos(iTime/3.0)+1.0)/2., (1.1+sin(iTime/5.0))/2.0, cos(sin(iTime)))*.1;
}
