const int steps = 2200;
const float threshold = 1000000000000.;

const vec2 pan = vec2(0.,0.);
const float zoom = .6;

int calculate(vec2 pos){

    vec2 z = vec2(0.);
    vec2 c = pos;
    vec2 buf;
    int i;
    for(i = 0; i<steps; i++){
        buf.x = z.x*z.x-z.y*z.y+c.x;
        buf.y = 2.*z.x*z.y+c.y;
        z = buf;
        if(length(z)>threshold){return i;}
    }

    return 0;
}
