float InverseLerp(float a, float b, float v) 
{
	return(v - a) / (b - a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float _Health = 1.0-mod(iTime*0.25,1.0);
    float _Health = abs(mod(iTime*0.25,1.0)*2.0-1.0);
    vec4 _ColorA = vec4(0,1,0,1);
    vec4 _ColorB = vec4(1,0,0,1);
    vec4 _ColorBG = vec4(1,1,1,1);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //health Bar

    float healthBarMask = float(_Health < uv.x);

    vec4 healthCol = mix(_ColorA, _ColorB, 1.0-clamp(_Health*1.6-0.3,0.0,1.0));

    vec4 bgColGradient = vec4(_ColorBG*1.0-sin(uv.y*3.14));

    // Output to screen
    //fragColor = vec4(col,1.0);
    vec4 outCol = mix(healthCol, bgColGradient, floor(healthBarMask));

    fragColor = outCol;
}
