vec3 adjust_brightnesscontrast( vec3 col , float B , float C )
{
    // adjust brightness
    col = clamp(col + vec3(B),vec3(0),vec3(1));

    // adjust contrast
    float CF = (1.01568627 * (C + 1.0)) / (1.01568627 - C);
    col = clamp(CF * (col-vec3(0.5)) + vec3(0.5), vec3(0),vec3(1));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float brightness = (iMouse.x / iResolution.x) * 2.0 - 1.0;
    float contrast = (iMouse.y / iResolution.y) * 2.0 - 1.0;

    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0, uv);

    col.rgb = adjust_brightnesscontrast( col.rgb , brightness , contrast );

    // Output to screen
    fragColor = vec4(col);
}
