#define M_PI 3.141592654

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float col = cos(M_PI * 2. * ((1. - uv.x) - 0.5)) * cos(M_PI * 2. * ((1. - uv.y) - 0.5)) / 2.2;

    // Output to screen
    fragColor = vec4(((vec3(.745, .494, .0431) + texture(iChannel0, uv * 4.).rgb) + textureLod(iChannel1, uv * 20., 4.).r) * col, 1.0);
}
