void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //ripple background
    vec3 ripple = texture(iChannel1, uv).rgb;

    //use square pattern
    vec3 col = vec3(cos(uv.x*200.0+1.0/sin(iTime)), cos(uv.y*200.0+(1.0/cos(iTime))),cos(uv.y*200.0));

    //use iChannel1 for the background
    vec3 bkgnd = texture(iChannel1, uv).rgb;

    // Time varying object color
    //col = col + vec3(.6 + .3*cos((iTime*2.0) + uv.x), 0.0, 0.0);

    // Time varying ripple color
    ripple = ripple + vec3(.6 + .3*cos((iTime*2.0) + uv.x), 0.0, 0.0);

    // Object
    if (uv.x > .3 && uv.x <.7 && uv.y > .2 && uv.y < .8){
        fragColor = vec4(col,1.0);
    }
    //ripple effect of background texture
    else if ((uv.x -.5)*(uv.x -.5) < .1*tan(iTime) - (uv.y - .5)*(uv.y - .5)/2.0
    && (uv.x -.5)*(uv.x -.5) > .06*tan(iTime) - (uv.y - .5)*(uv.y - .5)/2.0){
    fragColor = vec4(ripple,1.0);
    }
    //background texture
    else
    {
        fragColor = vec4(bkgnd, 1.0);
    }
}
