    bool circle(vec2 p, vec2 o, float r) {//рисуем круг(точка для проверки на принадлежность,центр,радиус круга)
     float kY = iResolution.y / iResolution.x;//поправичка для оси Y ,так как у нас прямоугольный экран 
      return (p.x - o.x) * (p.x - o.x) + (p.y - o.y) * (p.y - o.y)*( kY*kY) < r*r; 
    }



