#define d2(x) ((x)*(x))

bool circle(vec2 p, vec2 c, float r) {
               float scaleY = iResolution.y / iResolution.x;
                return d2(p.x - c.x) + d2(p.y - c.y) * d2(scaleY) < d2(r); 
    }
            float line(vec2 a, vec2 b, vec2 p) {
                           return (p.x - a.x) * (b.y - a.y) - (p.y - a.y) * (b.x - a.x);
            }

        bool check(vec2 a, vec2 b, vec2 c, vec2 p) {
                        return line(a, b, c) * line(a, b, p) >= 0.;
        }

    bool triangle(vec2 a, vec2 b, vec2 c, vec2 p) {
                 return  check(a, b, c, p) && check(b, c, a, p) && check(c, a, b, p);
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 crd = fragCoord/iResolution.xy;

      vec2 m = iMouse.xy/iResolution.xy;
      float pi = atan(0., -1.);
      float  a = atan(1., 1.); //0.01; //pi/4.0;
      float   c=cos(a);   
      float   s=sin(a);   
      crd.x -= 0.5;
      crd.y -= 0.5;
      float  x =  crd.x * c + crd.y * s;
      float  y = -crd.x * s + crd.y * c;
              x += 0.5;
              y += 0.5;

    fragColor = vec4(0.8, 0.7, 0, 1);

       if(circle(vec2(x,y), vec2(0.5, 0.5), 0.2))
         fragColor = vec4(0.8, 0.6, 0, 1.0);

       if(circle(vec2(x,y), vec2(0.57, 0.5), 0.03))
         fragColor = vec4(0, 0, 0, 1.0);

       if(circle(vec2(x,y), vec2(0.57, 0.51), 0.03))
         fragColor = vec4(0.8, 0.6, 0, 1.0);

       if(triangle(vec2(0.35, 0.8), vec2(0.65, 0.8), vec2(0.5, 0.95), vec2(x, y)))
         fragColor = vec4(1, 0, 0, 1.0);

       if(circle(vec2(x,y), vec2(0.5, 0.4), 0.04))
        fragColor = vec4(0.9, 0, 0.6, 1.0);

       if(triangle(vec2(0.9, 0.5), vec2(0.65, 0.6), vec2(0.65, 0.4), vec2(x, y)))
        fragColor = vec4(0.8, 0.6, 0, 1.0);

}
