vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 2.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    float time1 = iTime/64./2.;
    uv += vec2(time1/2.0,time1/3.0)/t1/4.0+(vec2(cos(time1),sin(time1)))*8./t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);
    float s1 = 1.;
    for(int k = 0; k < 6; k++){
        uv += t2;
        uv /= -scale;
        float s1 = sign(uv.x);
        uv.x += s1/2.;
        uv *= s1;
        t2 = -triangle_wave(uv-.5);
        t3 = triangle_wave(uv.yx);
        uv = t2-t3/(.5-s1);
        col.x = 1.+max(uv.y-uv.x,col.x/2.);
        col = abs(col.yzx+vec3(1.-col.x)*2.);
    }
    fragColor = vec4(col,1.0);
}
