vec3 rect(vec2 uv, vec3 color, float blur, float animSpeed, vec4 dim) {
    float anim = abs(sin(iTime * animSpeed)) * blur;

    vec2 bottom_left = vec2(smoothstep(dim.x-anim, dim.x+anim, uv.x), smoothstep(dim.y-anim, dim.y+anim, uv.y));
    vec2 top_right = vec2(1.0-smoothstep(dim.z-anim, dim.z+anim, uv.x), 1.0-smoothstep(dim.w-anim, dim.w+anim, uv.y));

    vec3 result = bottom_left.x * bottom_left.y * top_right.x * top_right.y * color;
    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 yellow = vec3(1.0, 0.0, 1.0);

    vec3 r1 = rect( uv, red, 0.09, 1.0, vec4(0.1, 0.8, 0.2, 0.9) );

    vec3 r2 = rect( uv, green, 0.06, 0.75, vec4(0.1, 0.6, 0.2, 0.7) );

    vec3 r3 = rect( uv, blue, 0.03, 0.5, vec4(0.3, 0.8, 0.4, 0.9) );

    vec3 r4 = rect( uv, yellow, 0.2, 2.0, vec4(0.3, 0.6, 0.4, 0.7) );

    vec3 col = r1 + r2 + r3 + r4;

    fragColor = vec4(col,1.0);
}
