const float Soft = 0.001;
const float Threshold = 0.3;

vec4 threshold(in vec2 fragCoord) {
    float f = Soft/2.0;
	float a = Threshold - f;
	float b = Threshold + f;

	vec4 tx = texture(iChannel0, fragCoord.xy/iResolution.xy);
	float l = (tx.x + tx.y + tx.z) / 3.0;

	float v = smoothstep(a, b, l);

	vec4 resultImage = vec4(v);
    return resultImage;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec4 baseColor = texture(iChannel1,fragCoord.xy/iResolution.xy);
	vec4 color = threshold(fragCoord);

    fragColor = (color * baseColor) + color * baseColor * sin(iTime*2.5);
}
