// Simplified and modified version of https://www.shadertoy.com/view/wtdSzX

// I needed to convert UV data to the integer coordinates of the hexes originating from the top left corner of
// the screen so I modified the original getHex function a little bit.

const vec2 sqr3 = vec2(1, 1.7320508); // Y value is the square root of 3.
const float horizontalHexCount = 16.; // How many hexes we want to place into the full image horizontally.

vec4 getHex(vec2 p)
{    
    p.y = 1. - p.y; // Up is 0 and bottom is 1 because the top-left hex coordinates is (0, 0).

    p *= horizontalHexCount; 
    // After this placement of N number of hexes horizontally, the magic numbers we use below are in integer hex coordinates.
    // 1 is 1 hex and .5 is half hex.

    p.y -= sqr3.y/3.; // We shift the vertical index by One Third of Square Root of 3 to make the top tip of the first hex touch the top of the screen.  
    p.x -= .5; // We shift the horizontal index left by half a hex because we want a full hex on (0, 0) coordinate rather than a half hex.

// vvv  This part is from the original shader.
    vec4 hC = floor(vec4(p, p - vec2(.5, 1))/sqr3.xyxy) + .5;

    vec4 h = vec4(p - hC.xy*sqr3, p - (hC.zw + .5)*sqr3);

    vec4 result = dot(h.xy, h.xy) < dot(h.zw, h.zw) 
        ? vec4(h.xy, hC.x - 1., hC.y) 
        : vec4(h.zw, hC.z, hC.w + .5);
// ^^^

    result.z += .5; // We revert the left shift we did earlier to get integer indexes like 1, 2, 3 instead of 1.5, 2.5, 3.5.

    // I don't know about the math used above but for some reason it returns the vertical hex indexes as half numbers like .5, 1, 1.5, 2 and so on.
    // We need to double them to get integer indexes like 1, 2, 3, 4.
    result.w *= 2.; 

    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.x; // Spread the full UV range (0 to 1) to width of the screen.
    uv.y += (iResolution.x - iResolution.y) / iResolution.x; // Shift the vertical UV range down so top-left corner becomes UV.Y = 1.
    // Uncomment this line to see the UV spread.
    //fragColor = vec4(uv, 0, 1); return;

    vec2 hex = getHex(uv).zw; // Convert UV to hex coordinates.   

    vec3 color = vec3(hex.x, hex.y, 0); // Convert hex coordinates to color.
    color = color / (horizontalHexCount - 1.); // Spread the colors horizontally so we can count the hex indices.

    fragColor = vec4(color, 1);    
}
