void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = texelFetch(iChannel0, ivec2(fragCoord),0).rgb;
    float n = voronoi3d(vec3((uv+vec2(iTime/20.0,0))*5.0,40.0))*voronoi3d(vec3((uv+vec2(iTime/20.0,0))*30.0,40.0+iTime/3.0))/2.0;
    col = smoothmix(vec3(1.0 - n*3.0), col, -0.01, 0.03, clamp(n-0.05,0.0,1.0));

    fragColor = vec4(col, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame <= 1){
        vec2 uv = fragCoord/iResolution.y;

        vec3 col = vec3(0,0,1);

        col = smoothmix(vec3(0,0.5,1), col, 0.1, 0.3, voronoi3d(vec3(uv*5.0,1.0))+voronoi3d(vec3(uv*10.0,1.0))/2.0-0.6);
        col = smoothmix(vec3(0.9,1.0,0.4), col, 0.05, 0.08, voronoi3d(vec3(uv*5.0,1.0))+voronoi3d(vec3(uv*10.0,1.0))/2.0-0.6);
        col = smoothmix(vec3(0.1,0.8,0), col, 0.0, 0.03, voronoi3d(vec3(uv*5.0,1.0))+voronoi3d(vec3(uv*10.0,1.0))/2.0-0.6);
        col = smoothmix(vec3(0.2,0.6,0.1), col, -0.08, 0.0, voronoi3d(vec3(uv*5.0,1.0))+voronoi3d(vec3(uv*10.0,1.0))/2.0-0.6);
        col = smoothmix(vec3(0,0.4,0.2), col, 0.0, 0.03, voronoi3d(vec3(uv*100.0,1.0))+mix(0.0,1.0,smoothstep(-0.1, -0.02, voronoi3d(vec3(uv*5.0,1.0))+voronoi3d(vec3(uv*10.0,1.0))/2.0-0.6))-0.6);

        fragColor = vec4(col,1);

    } else {
        fragColor = texelFetch(iChannel0, ivec2(fragCoord),0);
    }
}

//given a point, output a random point with values from 0 to 1
///  3 out, 3 in...
vec3 hash33(vec3 p3) //https://www.shadertoy.com/view/4djSRW
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

//https://iquilezles.org/articles/smin/
// power smooth min (k=8)
float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

float voronoi3d(vec3 uv){
    //assume uv is on a 0-1 scale
    float n = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            for (int z = -1; z < 2; z++){
                n = smin(n,distance(floor(uv) + vec3(x,y,z) + hash33(floor(uv) + vec3(x,y,z)),uv),8.0);
            }
        }
    }
    return n;
}

vec3 smoothmix(vec3 a, vec3 b, float l, float h, float t){
    return mix(a,b,smoothstep(l,h,t));
}
