#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    float i,e,g,z=6.;
    for(int j=0;j++<8;z-=.03)
        for(g=0.,i=0.;i++<70.;O+=.002/exp(i*i*e))
            p=d*g,
            p.z-=z,
            p=R(p,vec3(.577),iTime*.5),
            p=1.-abs(abs(p)-1.),
            e=length(p-clamp(p,-.3,.3))-.1,
            g+=e*.6;
}
