float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 O, in vec2 I )
{
    float t = iTime / 3.;
    vec2 p = (2.*I - iResolution.xy) / iResolution.y * 4. + t;
    vec2 f = fract(p);

    O = vec4(
        smoothstep(0.05,0.,mix(
            abs(abs(dot(f,vec2(-1,1)))-.5),
            abs(abs(dot(f,vec2(1,1))-1.)-.5),
            step(hash12(floor(p)+floor(t)),.5))));
}
