// set up constants, effects ripple behaviour
float violence = 0.02;
float levelOfExtreme = 5.0;

// function warp x or y coordinate based on time and sin that shit
float warp( float a , float timescale)
{
    return sin( (timescale * iTime + a) * levelOfExtreme ) * violence;
}

// main thing
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xx);

    //uv = uv * 3.;

    uv += 19.2 * vec2(warp(uv.y *.0122,0.012),warp(uv.x * .0122,0.012));

    uv -= vec2(warp(uv.y,1.),warp(uv.x,1.));

    uv +=0.2333* vec2(warp(uv.y *2.05,1.433),warp(uv.x *2.05,1.433));
    uv +=0.12666* vec2(warp(uv.y *4.01222,2.355),warp(uv.x *4.01222,2.355));

    vec2 t =  uv + 0.01 * texture(iChannel0, uv + iTime*.02).xy; 

	fragColor = texture(iChannel1, t);
}
