  bool circle(vec2 p, vec2 o, float r) {
   float scaleY = iResolution.y / iResolution.x;
    return (p.x - o.x) * (p.x - o.x) + (p.y - o.y) * (p.y - o.y) * (scaleY * scaleY) < r*r; 
  }

