void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Coords
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    uv.x *= ar; // make coords square

    // Shaping
    vec2 st = uv-vec2(.5*ar, .5);
    float r = 5.*length(st);
    float phi = atan(st.y/st.x);//+.001*iTime*r;
    float f = .5*cos(8.*r + .5*cos(50.*phi)+cos(r*iTime)) + .5;
    f = smoothstep(0., 1., f);

    //float f = rect(uv, vec2(.3, .3), vec2(.5, .5), .01);

    // Pixel Color
    vec3 col = vec3(66., 135., 245.)/255.; // + 0.5*+uv.xyx+vec3(0,2,4));
    col = .6*mix(vec3(5., .3, .2), vec3(0., 0.,1.000), f);


    // Output to screen
    fragColor = vec4(col,1.);
}
