#define blue vec4(0.0, 0.157, 0.408, 1)
#define red vec4(0.937, 0.169, 0.176, 1)
#define white vec4(1, 1, 1, 1)


// version with functions (better)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float yxRatio = iResolution.y / iResolution.x;

    // red
    float y = step(uv.y, 0.375) + step(0.625, uv.y);
    float x = step(uv.x, (0.375*yxRatio)) + step(0.625*yxRatio, uv.x);
    float w = 2.-y-x;

    fragColor = mix(red, white, w);

    // blue
    y = step(uv.y, 0.4375) + step(0.5625, uv.y);
    x = step(uv.x, 0.4375*yxRatio) + step(0.5625*yxRatio, uv.x);
    w = clamp(2.-x-y, 0., 1.);

    fragColor = mix(fragColor, blue, w);
}
