void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float c = texture(iChannel0, uv).r;

    // https://www.color-hex.com/color-palette/114761
    vec3 palette1[5] = vec3[5](
        vec3(  84,  13, 110 ) / 255.,
        vec3( 238,  66, 102 ) / 255.,
        vec3( 255, 210,  63 ) / 255.,
        vec3(  66, 191, 221 ) / 255.,
        vec3( 187, 230, 228 ) / 255.
    );

    // https://www.color-hex.com/color-palette/114398
    vec3 palette2[5] = vec3[5](
        vec3(  55,  90, 125) / 255.,
        vec3( 190, 110, 130) / 255.,
        vec3( 250, 175, 150) / 255.,
        vec3( 190, 110, 130) / 255.,
        vec3( 245, 115, 130) / 255.
    );


    vec3 col1 = palette1[0];
    vec3 col2 = palette2[0];
    float n = 1./4.;

    for (float i = 0.; i < 5.; i++) {
        float cut = smoothstep(n * i, n * (i + 1.), c);
        col1 = mix(col1, palette1[int(i)], cut);
        col2 = mix(col2, palette2[int(i)], cut);
    }

    float t = sin(iTime) * .5 + .5;
    fragColor = vec4(mix(col1, col2, t),1.0);
}
