// variant of https://shadertoy.com/view/NddyRH

#define T(U,l) textureLod(iChannel0, (U)/vec2(288./352.,1), l)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u / R * 3.*R.y/288.;                 // normalized coordinates

    O = T(U, 0.);                                 // high res texture luminance
    float lum = dot(O, vec4(.21,.72,.07,0) );

    ivec2 I = ivec2(U/vec2(288./352.,1)); int i = I.x + 4*I.y; // tiles id
    if (I.x> 3 ) { O = vec4(lum); return; }       // ( right: show lum )

    vec4 C = T(U, float(i));                      // lowres chrominance
    if ( i > 7 ) { O = T(U, float(i-8)); return; }// ( top: show it )
    C -= dot(C, vec4(.21,.72,.07,0) );

    O = lum + C;   // highres luminance + low-res chrominance

    if ( abs(U.y-2.)<1./48. ) O++;                // separator
}
