void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5; 
    uv.x*= iResolution.x/iResolution.y; 
    uv.x = fract(uv.x*5.-.5+sin(iTime+uv.y*3.+uv.x*1.3+uv.y*2.)-uv.y*14.);
    uv.x +=uv.y*.5;
    uv.y *= uv.y;

    float wave = uv.x*(clamp(0.,1.,uv.y+.9));
    vec3 color = vec3(.8 - uv.x*.4,.1 +uv.y,.3+uv.y*.9);
    vec3 col = vec3(wave * color);
    fragColor = vec4(col,1.);
}
