const vec3 lineColor = vec3(1.0, 1.0, 0.0);
const float speedRate = 1.0;
const float angle = radians(80.0);
const float distanceRate = 1.5;
const float widthRate = 0.1;
#define USE_ADD false

float plot(float d) {
	return smoothstep(widthRate, 0.0, abs(d));
}

float line(vec2 p, vec2 d) {
    return abs(dot(normalize(vec2(d.y, -d.x)), p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord / iResolution.xy) ;
    vec3 col = texture(iChannel0, uv).xyz;

    vec2 d = vec2(cos(angle), sin(angle));

    float t = mod(iTime * speedRate, distanceRate);
    uv.x = uv.x - t + (distanceRate - 1.0) * 0.5;
    float l = plot(line(uv, d));
    if(USE_ADD)
        fragColor = vec4(col + lineColor * l, 1.0);
    else    
        fragColor = vec4(mix(col, lineColor, l), 1.0);

}
