bool circle(vec2 p, vec2 c, float r)
{
    float scaleY = iResolution.y / iResolution.x;
     return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY) < r*r; 
}
bool oval(vec2 p, vec2 c, float r, float a, float b)
{
    float scaleY = iResolution.y / iResolution.x;
     return (p.x - c.x) * (p.x - c.x)*(p.x - c.x) * (p.x - c.x)/(a*a) + (p.y   - c.y) * (p.y - c.y)*(p.y   - c.y) * (p.y - c.y)/(b*b) * (scaleY * scaleY) < r*r; 
}

float calculate(vec2 a, vec2 b, vec2 p)
{
    return (p.x - a.x) * (b.y - a.y) - (p.y-a.y) * (b.x - a.x);
}

bool isOnOneSide(vec2 a, vec2 b, vec2 c, vec2 p) 
{
    return calculate(a, b, c) * calculate(a, b, p) >= 0.;
}

bool triangle(vec2 a, vec2 b, vec2 c, vec2 p)
{
    return  isOnOneSide(a, b, c, p) && isOnOneSide(b, c, a, p) && isOnOneSide(c, a, b, p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 crd = fragCoord/iResolution.xy;
     crd = crd - vec2(0.5);
      float x = crd.x;
      float y = crd.y;
    fragColor = vec4(1.0, 1.0, 1.0, 1.0);

       if(circle(vec2(x,y), vec2(0.0, -0.1), 0.15))
         fragColor = vec4(0.93, 0.9, 0.74, 1.0);
       if(circle(vec2(x,y), vec2(0.07, -0.03), 0.02))
         fragColor = vec4(0., 0, 0., 1.0);
       if(circle(vec2(x,y), vec2(0.07, -0.02), 0.02))
         fragColor = vec4(0.93, 0.9, 0.74, 1.0);
       if(oval(vec2(x,y), vec2(0., 0.15), 0.01, 1.7, 0.05))
         fragColor = vec4(1, 0., 0., 1.0);
       if(triangle(vec2(0.0, 0.3), vec2(-0.1, 0.17), vec2(0.1, 0.17), vec2(x, y)))
         fragColor = vec4(1, 0., 0., 1.0);
       if(circle(vec2(x,y), vec2(0.0, 0.34), 0.025))
        fragColor = vec4(1., 0., 0., 1.0);
       if(circle(vec2(x,y), vec2(0.04, -0.18), 0.035))
        fragColor = vec4(1., 0., 0.4, 1.0);
       if(triangle(vec2(0.12, -0.26), vec2(0.065, -0.22), vec2(0.12, -0.266), vec2(x, y)))
        fragColor = vec4(0.6, 0.6, .3, 1.0);
       if(triangle(vec2(0.3, -0.1), vec2(0.1, -0.15), vec2(0.1, -0.05), vec2(x, y)))
        fragColor = vec4(0.93, 0.9, 0.74, 1.0);
}
