vec3 sample_tx(vec2 pos)
{
    vec2 o = 3.0/iChannelResolution[0].xy;

    vec3 col = texture(iChannel0, pos).rgb * 2.0;
    col += texture(iChannel0, pos + vec2(o.x, 0.0)).rgb;
    col += texture(iChannel0, pos + vec2(-o.x, 0.0)).rgb;
    col += texture(iChannel0, pos + vec2(0.0, o.y)).rgb;
    col += texture(iChannel0, pos + vec2(0.0, -o.y)).rgb;

    return col / 6.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = vec2(sin(iTime*0.1), 13.0*cos(iTime*0.1))*1.1;
    vec2 uv = (fragCoord-0.5*(iResolution.xy))/iResolution.x;

    float ty = atan(uv.x, uv.y);
    float tx = length(uv);
    vec2 st =  vec2(ty/3.1415926535, 2.0 - (0.75 / tx)*0.1);

    vec3 col = sample_tx(st+pos);//texture(iChannel0, st + pos).rgb;

    fragColor = vec4(col,1.0);
}
