#define flip(v,pct) mix(v, 1.-v,pct)
#define fill(x,s) step(x,s)
#define stroke(x,s,w) smoothstep(6./iResolution.y, 0., abs(s-x) - w*.5 )

float rectSDF(vec2 st, vec2 s)
{
    return max(abs(st.x/s.x),abs(st.y/s.y));
}

float crossSDF(vec2 st, float s)
{
    vec2 size = vec2(.25,s);
    return min(rectSDF(st,size.xy), rectSDF(st,size.yx));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float color = 0.;
    vec2 st = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float rect = rectSDF(st, vec2(.5));
    color += fill(rect,1.);
    float crosssdf = crossSDF(st, clamp(abs(sin(iTime))+.3,.6,1.));
    color*= step(.5,fract(crosssdf*4.+iTime));
    color*= step(1., crosssdf);
    color+=fill(crosssdf,.5);
    color += stroke(rect,1.1,.05);
    color += stroke(rect,1.2,.025);
    color = flip(color, stroke((cos(iTime)*st.x+st.y)*2., 0., .5));
    //color.rgb = vec3(fract(fragCoord.x/10.)) * vec3(fract(fragCoord.y/10.)); // GridDebug
    fragColor = vec4(vec3(color),1.0);
}
