#define GRID_SIZE 10.0        // world grid size scaler

#define GRID_THIN 0.1f    // > is thicker, < is thinner (grid lines)


vec2 rotate( in vec2 p, in float angle )
{
	#define fma(a,b,c) (a*b+c)
    #define c_ x
	#define s_ y

    vec2 cossin = vec2(cos(angle), sin(angle));
	return( vec2(fma(p.x, cossin.c_, p.y * cossin.s_), fma(p.x, -cossin.s_, p.y * cossin.c_)) );

	#undef c_
	#undef s_
    #undef fma
}

float grid( in vec2 uv, in float scale, in float thin )
{
    vec2 tiledUV = fract(uv*scale+0.5f);
    vec2 square = -abs(tiledUV*2.-1.);

    float d = abs(min(square.x, square.y));

    return 1.0f - exp2(-2.7 * (d - thin) * (d - thin));
    //return smoothstep(1.0f - thin, 1.0f, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv - 0.5f;
    //uv = 2.0f * uv - 1.0f;

    uv = rotate(uv, iTime * 0.125f);

    float result = grid(uv, GRID_SIZE, GRID_THIN);

    float d = mix(-2.0f, -5.0f + iTime * 0.5f, abs(sin(iTime))) * dot(textureLod(iChannel0, uv + iTime*0.1f, 0.0f).rgb, vec3(0.5f));

    for ( float i = 1.0f ; i < 10.0f ; ++i ) {
        result = mix(result, grid(uv + d * vec2(i/iResolution.y), GRID_SIZE, GRID_THIN), 0.5f);
    }

    result = sqrt(result);

    fragColor = vec4(vec3(result), 1.0f); // gamma correct
}
